var MyFirstSI = Class.create();
MyFirstSI.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	type: 'MyFirstSI',
	helloWorld:function() { 

		var gr=new GlideRecord('sys_user');
		var sysid=this.getParameter('sysparam_user_name');
		gr.addQuery('sys_id',sysid);
		gr.query();
		var phone ='';

		if(gr.next()){
			gr.AddInfoMessage("It s here"+gr.mobile_phone);
			phone=gr.mobile_phone;
			if(phone!='')
				return phone;
			return 'No mobile phone';


		}






	}
	});

